import React from 'react';
import ReactDOM from 'react-dom';
import './greeting.scss';

function setData() {
	localStorage.setItem('userName', prompt('Enter Your Name:'));
}

function getData() {
	let yourName = localStorage.getItem('userName');
	console.log(yourName);
}

function App() {
	const date = new Date();
	const hours = date.getHours();
	const rightNow = date.toLocaleTimeString();

	let timeOfDay;

	if (hours < 11) {
		timeOfDay = 'Good Morning, ';
	} else if (hours >= 11 && hours < 17) {
		timeOfDay = 'Good Afternoon, ';
	} else {
		timeOfDay = 'Good Evening, ';
	}

	return (
		<div className="greeting">
			<h1>{timeOfDay + yourName + '!'}</h1>
			<h2>{rightNow}</h2>
			<h1>Hello from greeting.jsx</h1>
		</div>
	);
}

function Tick() {
	ReactDOM.render(<App />, document.getElementById('greeting'));
}

setInterval(Tick, 1000);

export default Tick;
