import React, { Component } from 'react';
import './shoppingItem.scss';
export default class ShoppingItem extends Component {
	render() {
		const { item } = this.props;

		return (
			<div className='item-container' onClick={this.toggleItem}>
				<h4 className={'shopping-item' + (item.completed ? ' completed' : '')}>{item.text}</h4>
				<i className='fas fa-trash-alt' onClick={this.props.item(localStorage.removeItem('item'))}></i>
			</div>
		);
	}

	toggleItem = () => {
		this.props.updateItemFn(this.props.item);
	};
}
