import React, { Component } from 'react';

export default class ShoppingItem extends Component {
	render() {
		const { item } = this.props;

		return <div onClick={this.toggleItem}>{item.text}</div>;
	}

	toggleItem = () => {
		this.props.updateItem(this.props.item);
	};
}
